mapper.update:
UPDATE tbl_comment
SET comment_content = #{commentContent}, comment_update_date = current_timestamp
WHERE user_num = #{userNum} AND comment_num = #{commentNum}

mapper.select:
SELECT comment_num, IFNULL(comment_parent_num, comment_num) comment_parent_num, comment_parent_id, comment_content, 
comment_register_date, comment_update_date, u.user_num, board_num, user_id
FROM tbl_user u join tbl_comment c on u.user_num = c.user_num AND board_num = #{boardNum}
ORDER by comment_parent_num, comment_num
LIMIT 0, #{criteria.rowCount}

SELECT count(comment_num) FROM
	(
		SELECT comment_num, comment_parent_num, comment_parent_id, comment_content, 
		comment_register_date, comment_update_date, u.user_num, board_num, user_id
		FROM tbl_user u join tbl_comment c on u.user_num = c.user_num AND board_num = #{boardNum}
		LIMIT #{criteria.offset}, #{criteria.amount}
	) c
